// HU-01: Validaci√≥n de campos requeridos
const requiredFields = ['nombre', 'rut', 'carrera', 'asignatura', 'institucionOrigen'];
const errors = [];
const data = $input.item.json.body || $input.item.json;

// Validar campos obligatorios
for (const field of requiredFields) {
  if (!data[field] || data[field].toString().trim() === '') {
    errors.push(`Campo requerido: ${field}`);
  }
}

// Validar que haya archivo
if (!data.file && !data.documentos) {
  errors.push('Campo requerido: archivo PDF');
}

// Si hay errores, retornar respuesta de error
if (errors.length > 0) {
  return {
    json: {
      success: false,
      errors: errors,
      message: 'Faltan campos obligatorios',
      code: 400
    }
  };
}

// Si todo est√° bien, procesar y agregar metadatos
const result = Object.assign({}, data, {
  success: true,
  documentosCompletos: true,
  fechaSolicitud: new Date().toISOString(),
  id: 'SOL-' + data.rut.replace(/[^0-9]/g, '') + '-' + Date.now(),
  estado: 'Recibida',
  // Preservar datos del archivo
  fileName: data.file ? data.file.filename : data.documentos,
  fileData: data.file ? data.file.data : null,
  fileMimeType: data.file ? data.file.mimeType : 'application/pdf'
});

return { json: result };

// Validar formato de RUT chileno
const data = $input.item.json;

function validarRUT(rut) {
  if (!rut) return false;

  // Eliminar puntos y gui√≥n
  rut = rut.toString().replace(/\./g, '').replace(/-/g, '').toUpperCase();

  // Verificar formato b√°sico (7-8 d√≠gitos + verificador)
  if (!/^[0-9]{7,8}[0-9K]$/.test(rut)) {
    return false;
  }

  // Separar n√∫mero y d√≠gito verificador
  const cuerpo = rut.slice(0, -1);
  const dv = rut.slice(-1);

  // Calcular d√≠gito verificador
  let suma = 0;
  let multiplicador = 2;

  for (let i = cuerpo.length - 1; i >= 0; i--) {
    suma += parseInt(cuerpo[i]) * multiplicador;
    multiplicador = multiplicador === 7 ? 2 : multiplicador + 1;
  }

  const dvEsperado = 11 - (suma % 11);
  const dvCalculado = dvEsperado === 11 ? '0' : dvEsperado === 10 ? 'K' : dvEsperado.toString();

  return dv === dvCalculado;
}

// Validar si existe el campo success
if (!data.success) {
  return { json: data };
}

const rutValido = validarRUT(data.rut);

if (!rutValido) {
  return {
    json: {
      success: false,
      errors: ['RUT inv√°lido: ' + data.rut],
      message: 'El RUT proporcionado no es v√°lido',
      code: 400,
      detalles: 'Formato esperado: 12.345.678-9 o 12345678-9'
    }
  };
}

// RUT v√°lido, continuar
return { json: data };

// Validar formato de email
const data = $input.item.json;

function validarEmail(email) {
  if (!email || email.trim() === '') {
    return true; // Email es opcional
  }

  // Regex para validar email
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  if (!emailRegex.test(email)) {
    return false;
  }

  // Validar longitud
  if (email.length > 254) {
    return false;
  }

  return true;
}

// Validar si existe el campo success
if (!data.success) {
  return { json: data };
}

const emailValido = validarEmail(data.email);

if (!emailValido) {
  return {
    json: {
      success: false,
      errors: ['Email inv√°lido: ' + data.email],
      message: 'El email proporcionado no es v√°lido',
      code: 400
    }
  };
}

// Email v√°lido, continuar
return { json: data };

// HU-01: Validar formato de documentos PDF
const data = $input.item.json;
const fileName = data.fileName || data.documentos || '';
let pdfsOk = true;
let badFiles = [];

// Validar extensi√≥n
if (typeof fileName === 'string') {
  if (!fileName.toLowerCase().endsWith('.pdf')) {
    pdfsOk = false;
    badFiles.push(fileName);
  }
} else if (Array.isArray(fileName)) {
  for (const doc of fileName) {
    const name = doc.fileName || doc.name || doc;
    if (typeof name === 'string' && !name.toLowerCase().endsWith('.pdf')) {
      pdfsOk = false;
      badFiles.push(name);
    }
  }
}

// Validar tipo MIME si existe
if (data.fileMimeType && data.fileMimeType !== 'application/pdf') {
  pdfsOk = false;
  badFiles.push(`Tipo MIME incorrecto: ${data.fileMimeType}`);
}

const result = Object.assign({}, data, {
  pdfsOk: pdfsOk,
  badFiles: badFiles,
  validacionPDF: pdfsOk ? 'Documentos v√°lidos' : 'Documentos inv√°lidos'
});

return { json: result };

// Validar tama√±o del archivo PDF (max 10MB)
const data = $input.item.json;
const MAX_SIZE_MB = 10;
const MAX_SIZE_BYTES = MAX_SIZE_MB * 1024 * 1024;

let sizeOk = true;
let sizeError = '';

// Si tenemos datos del archivo
if (data.fileData) {
  // Calcular tama√±o del base64
  const base64Length = data.fileData.length;
  const fileSizeBytes = (base64Length * 3) / 4;

  if (fileSizeBytes > MAX_SIZE_BYTES) {
    sizeOk = false;
    sizeError = `Archivo muy grande: ${(fileSizeBytes / 1024 / 1024).toFixed(2)}MB. M√°ximo: ${MAX_SIZE_MB}MB`;
  }

  // Agregar tama√±o al objeto
  data.fileSize = fileSizeBytes;
  data.fileSizeMB = (fileSizeBytes / 1024 / 1024).toFixed(2);
}

const result = Object.assign({}, data, {
  sizeOk: sizeOk,
  sizeError: sizeError,
  validacionTama√±o: sizeOk ? 'Tama√±o v√°lido' : 'Archivo muy grande'
});

return { json: result };

parameters.jsCode:
// Convertir base64 a binario para Google Drive
const items = $input.all();
const results = [];

for (const item of items) {
  const data = item.json;

  // Obtener datos del archivo
  const base64Data = data.fileData || (data.file ? data.file.data : null);
  const fileName = data.fileName || (data.file ? data.file.filename : 'documento.pdf');
  const mimeType = data.fileMimeType || (data.file ? data.file.mimeType : 'application/pdf');

  if (!base64Data) {
    throw new Error('No se encontraron datos del archivo para subir a Drive');
  }

  // Convertir base64 a buffer binario
  const binaryBuffer = Buffer.from(base64Data, 'base64');

  // Crear objeto con datos binarios para Google Drive
  results.push({
    json: data,
    binary: {
      data: {
        data: binaryBuffer.toString('base64'),
        mimeType: mimeType,
        fileName: fileName,
        fileExtension: 'pdf'
      }
    }
  });
}

return results;

