// Sincronizar y preservar el ID original generado al inicio
const allItems = $input.all();

console.log('=== SINCRONIZAR - Total items:', allItems.length);

// PASO 1: Buscar el item que tiene el ID original (formato SOL-XXXXX-timestamp)
let itemConIdOriginal = null;
let itemConDatosCompletos = null;

for (const item of allItems) {
  const id = item.json.id;

  console.log('Item analizado:', {
    id: id,
    tiene_id: !!id,
    formato_correcto: id && id.startsWith('SOL-') && id.split('-').length === 3,
    tiene_linkDrive: !!item.json.linkDrive,
    campos_totales: Object.keys(item.json).length
  });

  // Verificar si este item tiene el ID en formato correcto
  if (id && id.startsWith('SOL-') && id.split('-').length === 3) {
    itemConIdOriginal = item;
    console.log('‚úÖ ID ORIGINAL ENCONTRADO:', id);
  }

  // Tambi√©n guardar el item con m√°s campos (backup)
  if (!itemConDatosCompletos || Object.keys(item.json).length > Object.keys(itemConDatosCompletos.json).length) {
    itemConDatosCompletos = item;
  }
}

// PASO 2: Fusionar datos
if (itemConIdOriginal && itemConDatosCompletos && itemConIdOriginal !== itemConDatosCompletos) {
  // Combinar: ID del original + otros datos del m√°s completo
  console.log('üîÄ Fusionando datos de m√∫ltiples items');

  const merged = {
    json: {
      ...itemConDatosCompletos.json,
      id: itemConIdOriginal.json.id,
      fechaSolicitud: itemConIdOriginal.json.fechaSolicitud || itemConDatosCompletos.json.fechaSolicitud
    }
  };

  console.log('=== ITEM FINAL - ID:', merged.json.id);
  return merged;
}

// PASO 3: Si encontramos el ID original, usarlo
if (itemConIdOriginal) {
  console.log('=== USANDO ITEM CON ID ORIGINAL:', itemConIdOriginal.json.id);
  return itemConIdOriginal;
}

// PASO 4: Si no, usar el m√°s completo
console.warn('‚ö†Ô∏è No se encontr√≥ ID original, usando item m√°s completo');
console.log('=== ITEM SELECCIONADO (backup) - ID:', itemConDatosCompletos?.json.id);
return itemConDatosCompletos || $input.last();

