// Respuesta exitosa tras procesar la solicitud
const data = $input.item.json;

console.log('===== FUNCTION SUCCESS RESPONSE =====');
console.log('Datos completos recibidos:', JSON.stringify(data, null, 2));

// Buscar el ID en TODAS las ubicaciones posibles con prioridad
let solicitudId = null;

// Buscar primero en ubicaciones directas
if (data.id) solicitudId = data.id;
else if (data.solicitudId) solicitudId = data.solicitudId;
else if (data.ID) solicitudId = data.ID;
else if (data.idSolicitud) solicitudId = data.idSolicitud;
else if (data.solicitud_id) solicitudId = data.solicitud_id;

// Si no encontr√≥, buscar en objeto sheets
if (!solicitudId && data.sheets) {
  if (data.sheets.id) solicitudId = data.sheets.id;
  else if (data.sheets.solicitudId) solicitudId = data.sheets.solicitudId;
}

// Si a√∫n no encontr√≥, buscar en range
if (!solicitudId && data.range) {
  const match = data.range.match(/\d+/);
  if (match) {
    solicitudId = `SOL-${match[0]}`;
  }
}

// √öltimo recurso: generar uno temporal
if (!solicitudId) {
  const timestamp = Date.now();
  solicitudId = `SOL-TEMP-${timestamp}`;
  console.warn('‚ö†Ô∏è NO SE ENCONTR√ì ID - Generando temporal:', solicitudId);
}

console.log('ID FINAL seleccionado:', solicitudId);
console.log('=====================================');

return {
  json: {
    success: true,
    message: 'Solicitud recibida y procesada correctamente',
    id: solicitudId,
    fechaSolicitud: data.fechaSolicitud || data.fecha || new Date().toISOString(),
    estado: data.estado || 'Recibida',
    linkDrive: data.linkDrive || '',
    driveFileId: data.driveFileId || '',
    proximosPasos: 'Recibir√° notificaci√≥n por email con el estado de su solicitud',
    tiempoEstimado: '5-10 d√≠as h√°biles',
    code: 200
  }
};
