parameters.functionCode:
// HU-01: Validación de campos requeridos
const requiredFields = ['nombre', 'rut', 'carrera', 'asignatura', 'institucionOrigen', 'documentos'];
const errors = [];
const data = $input.item.json.body || $input.item.json;

// Validar campos obligatorios
for (const field of requiredFields) {
  if (!data[field] || data[field].toString().trim() === '') {
    errors.push(`Campo requerido: ${field}`);
  }
}

// Si hay errores, retornar respuesta de error
if (errors.length > 0) {
  return {
    json: {
      success: false,
      errors: errors,
      message: 'Faltan campos obligatorios',
      code: 400
    }
  };
}

// Si todo está bien, procesar y agregar metadatos
const result = Object.assign({}, data, {
  success: true,
  documentosCompletos: true,
  fechaSolicitud: new Date().toISOString(),
  id: 'SOL-' + data.rut.replace(/[^0-9]/g, '') + '-' + Date.now(),
  estado: 'Recibida'
});

return { json: result };

// HU-01: Validar formato de documentos PDF
const data = $input.item.json;
const docs = data.documentos;
let pdfsOk = true;
let badFiles = [];

// Si documentos es string, asumir que es nombre de archivo
if (typeof docs === 'string') {
  if (!docs.toLowerCase().endsWith('.pdf')) {
    pdfsOk = false;
    badFiles.push(docs);
  }
} else if (Array.isArray(docs)) {
  // Si es array, validar cada archivo
  for (const doc of docs) {
    const fileName = doc.fileName || doc.name || doc;
    if (typeof fileName === 'string' && !fileName.toLowerCase().endsWith('.pdf')) {
      pdfsOk = false;
      badFiles.push(fileName);
    }
  }
}

const result = Object.assign({}, data, {
  pdfsOk: pdfsOk,
  badFiles: badFiles,
  validacionPDF: pdfsOk ? 'Documentos válidos' : 'Documentos inválidos'
});

return { json: result };

// HU-01: Preparar datos LIMPIOS para Solicitudes
// Solo campos relevantes, SIN campos técnicos
const data = $input.item.json;

return {
  json: {
    id: data.id,
    fecha: data.fechaSolicitud,
    estudiante: data.nombre,
    rut: data.rut,
    carrera: data.carrera,
    asignatura: data.asignatura,
    institucionOrigen: data.institucionOrigen,
    documentos: data.documentos,
    email: data.email || '',
    estado: data.estado
  }
};

// HU-01: Preparar datos LIMPIOS para Log
// Solo información relevante del evento
const data = $input.item.json;

return {
  json: {
    timestamp: data.fechaSolicitud,
    id: data.id,
    evento: 'Solicitud Recibida',
    estudiante: data.nombre,
    estado: data.estado,
    detalles: 'HU-01: Recepción exitosa - Asignatura: ' + data.asignatura + ' - Institución: ' + data.institucionOrigen
  }
};

