// Respuesta de error para solicitudes inválidas
const data = $input.item.json;

return {
  json: {
    success: false,
    message: data.message || 'Error en la validación',
    errors: data.errors || ['Error desconocido'],
    code: data.code || 400
  }
};

parameters.jsCode:
// Preservar datos originales de la solicitud
// Buscar el item que tiene los datos completos (viene de IF - PDF Válido)
for (const item of $input.all()) {
  if (item.json.id && item.json.fechaSolicitud && item.json.nombre) {
    return item;
  }
}

// Si no se encuentra, retornar el primer item
return $input.first();

